  const res = await axios.get('https://example.com/api/result');
  return res.data.cid;
}

const SimpleProgram = ZkProgram({
  name: 'Calling an async method',
  publicOutput: CircuitString,

  methods: {
    call: {
      privateInputs: [],
      async method() {
        console.log('Here 1');
        const value = await getValue();
        const toReturn = CircuitString.fromString(value);
        return toReturn;
        // console.log(value);
      },
    },
  },
});

const { verificationKey } = await SimpleProgram.compile();
const proof = await SimpleProgram.call();
console.log(proof);

const ok = await verify(proof.toJSON(), verificationKey);
console.log(ok);
```

