import { Bool, Provable, Struct, UInt64 } from "snarkyjs";

export class OptionalUInt64 extends Struct({
  // if this is false, we treat it as a dummy entry
  isSome: Bool,
  value: UInt64,
}) {}

export class Batch extends Struct({
  batch: Provable.Array(OptionalUInt64, 4),
}) {}

function reduce({ batch }: Batch): UInt64 {
  // we can use reduce because we are iterating over a fixed-size batch 
  return batch.reduce((result, number) => {
    // if the entry in the batch is a dummy, we skip it and return the old result instead
    return Provable.if(number.isSome, result.add(number.value), result);
  }, UInt64.from(0));
}

const result = reduce(
  new Batch({
    batch: [
      {
        isSome: Bool(true),
        value: UInt64.from(1),
      },
      {
        isSome: Bool(true),
        value: UInt64.from(1),
      },
      // rest are just dummy values,
      {
        isSome: Bool(false),
        value: UInt64.zero,
      },
      {
        isSome: Bool(false),
        value: UInt64.zero,
      },
    ],
  })
);

console.log("result", result.toBigInt());
